site_customizer_data.custom_css:
.main-navigation .menu>li {
    border-bottom: 0px;
}

.responsive-block-editor-addons-block-post-grid .responsive-block-editor-addons-block-post-grid-author, .responsive-block-editor-addons-block-post-grid .responsive-block-editor-addons-block-post-grid-date {

	letter-spacing: 2px;
}

.responsive-block-editor-addons-block-post-grid-more-link {
	padding: 15px 30px;
    width: fit-content;
	  background-color: #CB8161;
}
.responsive-block-editor-addons-block-post-grid-more-link:hover {
    background-color: #181818;
}

.woocommerce-error, .woocommerce-info, .woocommerce-message{
	background-color: #f6fdde;
}

#woo-popup-wrap .woocommerce-Price-amount bdi {
    color: #7e7e7e;
}

#woo-popup-wrap .buttons-wrap a.continue-btn{
	font-size:16px;
	font-weight:400;
	border-radius:0px;
	padding:12px 30px;
}

#woo-popup-wrap .buttons-wrap a.cart-btn{
	font-size:16px;
	font-weight:400;
	border-radius:0px;
	padding:12px 30px;
}

.woocommerce .widget_price_filter .ui-slider .ui-slider-handle, .woocommerce .widget_price_filter .ui-slider .ui-slider-range, .wc-block-grid__product-onsale, .woocommerce span.onsale{
	background-color: #f25b1e;
	font-weight:500;
	border-radius:10px;
	min-height: 0px;	
	line-height:1;
	padding: 7px 10px;
	text-transform:capitalize;

}

.woocommerce ul.products li.product .onsale {
	font-weight:500;
	border-radius:10px;
	min-height: 0px;
	min-width:0px;
	line-height:1;
	padding: 7px 10px;
}

.woocommerce a.remove {
	line-height:1;
}

@media only screen and (min-width: 769px){
.woocommerce-account .row article#post-8{
	max-width: 70%;
	margin-left:auto;
	margin-right: auto;	
}
}

.wc-block-grid__product-rating .star-rating span:before, .wc-block-grid__product-rating .wc-block-grid__product-rating__stars span:before{
	color:#f7bc3d;
}

.wc-block-grid__product-title{
	color:#181818;
	font-weight:400;
}

.wc-block-grid__product-price {
	font-size:13.712px;
	font-weight:700;
}

.wc-block-grid__product-add-to-cart.wp-block-button .wp-block-button__link{
	padding:15px 30px;
	text-transform:uppercase;
}

.woocommerce #respond input#submit, .woocommerce a.button, .woocommerce button.button, .woocommerce input.button{
	font-weight:400;
	padding:15px 30px;
}

div.wpforms-container-full .wpforms-form .wpforms-field-label{
	font-size:15px;
	font-weight:400;
}

.responsive-contact-form .wpforms-submit{
	width: -webkit-fill-available !important;
	padding:15px 30px !important;
}

.responsive-contact-form .wpforms-field-large{
	font-size:15px !important;
	height:50px !important;
	padding:12px 20px !important;
}

.responsive-contact-form .wpforms-field-medium{
	font-size:15px !important;
	height:188px !important;
	padding:12px 20px !important;
}

.responsive-newsletter .wpforms-field-container{
	width:70% !important;
}

.responsive-newsletter .wpforms-submit-container{		
	width: 30% !important;
	padding:0 !important;
}

.responsive-newsletter .wpforms-field-large{
	height:50px !important;
	padding:12px 10px !important;
}

.responsive-newsletter .wpforms-field{
	padding:0 !important;
}

.responsive-newsletter .wpforms-submit{	
	font-size:14px !important;
	background-color: #181818 !important;
	height:50px !important;
	padding:0px !important;
}

.responsive-newsletter .wpforms-submit:hover{
	background-color: #A05A3C !important;
}
