}

.price_slider_amount .button{
	background-color: transparent !important;
	border: solid 2px #4E00FF !important;
	color: #4E00FF !important;
}

.price_slider_amount .button:hover{
	background-color: #4E00FF !important;
	color: #FFF !important;
}

.price_slider_wrapper .ui-slider-range {
    background-color: black !important;
}

.price_slider_wrapper .ui-widget-content {
    background-color: #666 !important;
}

.price_slider_wrapper .ui-slider-handle{
	background-color: #4E00FF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs::before{
	border-color: #4E00FF !important;
	border-width: 2px !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li.active{
	border-color: #4E00FF !important;
	border-width: 2px !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li{
	border-color: #4E00FF !important;
	border-width: 2px !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li::before{
	margin-left: -0.5px !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li.active{
	background-color: #4E00FF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li{
	background-color: #FFF  !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li:hover{
	background-color: #4E00FF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li a{
	color: #4E00FF  !important;
	background-color: #FFF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li.active a{
	color: #FFF  !important;
	background-color: #4E00FF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li a:hover{
	color: #4E00FF !important;
	background-color: #FFF !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li::before{
	box-shadow: unset !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li::after{
	box-shadow: unset !important;
}

.woocommerce div.product .woocommerce-tabs ul.tabs li:hover a{
	background-color: #4E00FF !important;
	color: #FFF !important;
}

.resp-counter-image-1 img{
	padding: 34.25px 21.93px;
	border: 0 !important;
	border-radius: 0 !important;
}

.resp-counter-image-1 .responsive-block-editor-addons-ifb-image-content,.resp-counter-image-3 .responsive-block-editor-addons-ifb-image-content{
	border: solid 6px #4e00ff;
	border-radius: 50%;
	background-color: #FFF !important;
}

.resp-counter-image-2 .responsive-block-editor-addons-ifb-image-content,.resp-counter-image-4 .responsive-block-editor-addons-ifb-image-content{
	border: solid 6px #15f3ed;
	border-radius: 50%;
	background-color: #FFF !important;
}

.resp-counter-image-2 img{
	padding: 24.37px 24.37px 19.73px 19.73px;
	border: 0 !important;
	border-radius: 0 !important;
}

.resp-counter-image-3 img{
	padding: 24.37px 17.36px 29.97px 17.36px;
	border: 0 !important;
	border-radius: 0 !important;
}

.resp-counter-image-4 img{
	padding: 24.37px 27.79px;
	border: 0 !important;
	border-radius: 0 !important;
}

#resp-counter-section .responsive-block-editor-addons-ifb-image-content{
	margin-top: -56px;
}

#resp-counter-section .responsive-column-wrap,#resp-counter-section .wp-block-responsive-block-editor-addons-info-block{
	overflow: visible !important;
}

#resp-counter-section .responsive-block-editor-addons-advanced-column-child .responsive-block-editor-addons-advanced-column-child:first-child .responsive-column-wrap{
	background: radial-gradient(circle, rgba(127,70,255,1) 0%, rgba(78,0,255,1) 100%);
}

#resp-counter-section .responsive-block-editor-addons-advanced-column-child .responsive-block-editor-addons-advanced-column-child:last-child .responsive-column-wrap{
	background: radial-gradient(circle, rgba(12,242,242,1) 0%, rgba(116,255,186,1) 0%, rgba(0,226,255,1) 100%);
}

#resp-counter-title{
	margin-top: -10px;
}
